import React, {useState}from 'react';
import { StyleSheet, View,Text, TextInput, TouchableHighlight } from 'react-native';
import firebase from "firebase"
import { TouchableOpacity } from 'react-native-gesture-handler';
import { CommonActions } from '@react-navigation/native'

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#FFFDF6",
    width: "100%",
    paddingTop: 72,
    paddingRight: 24,
    paddingLeft: 24,
  },
  input: {
    height: 48,
    marginBottom:24,
    backgroundColor: "#eee",
    borderWidth: 1,
    borderColor: "#DDD",
    padding: 8,
    lineHeight: 24,
    fontSize: 18,
    borderRadius: 6,
  },
  button: {
    backgroundColor: "#E31676",
    height: 48,
    borderRadius: 14,
    justifyContent: "center",
    alignItems: "center",
    width: "80%",
    alignSelf: "center",
    marginTop: 18,
  },
  buttonTitle: {
    fontSize:20,
    color: "#fff",
  },
  toSignup :{
    marginTop: 18,
    alignSelf: "center",
    borderBottomWidth:1,
  },
  signupText:{
    fontSize:20,
  }

});


const LoginScreen = (props) => {

  const [email,setEmail] =useState("k.157.2aic@gmail.com")
  const [password,setPassword] =useState("k-a.157.2")

  const handleLogin = () =>{
    firebase.auth().signInWithEmailAndPassword(email, password)
      .then(()=>{
        console.log("success")
         const resetAction = 
           CommonActions.reset({
             index:0,
             routes: [
               {name: "Home"}
             ],
           })
         props.navigation.dispatch(resetAction)
      })

      .catch((error)=>{
        console.log(error)
        console.log("error")
      })
  }
  const resetPassword =()=>{
    const auth = firebase.auth();
    // const emailAddress = "user@example.com";

    auth.sendPasswordResetEmail(email).then(function() {
      // Email sent.
      alert(`${email}にメールを送信しました。`)
    }).catch(function(error) {
      // An error happened.
      console.log(error)
    });
  }

  const switchToSignup = ()=>[
    props.navigation.navigate("Signup")
  ]

  return (
    <View style={styles.container}>

      <TextInput style={styles.input} value={email} placeholder="Email" 
        onChangeText={text => setEmail(text)} autoCapitalize="none" autoCorrect={false}/>
      <TextInput style={styles.input} value={password} placeholder="Password" 
       onChangeText={text=>setPassword(text)} autoCapitalize="none" autoCorrect={false}
       secureTextEntry={true}/>

      <TouchableOpacity  style={styles.toSignup} onPress={resetPassword}>
        <Text style={styles.signupText}>
          パスワードを忘れた方はこちら
        </Text>
      </TouchableOpacity>

      <TouchableHighlight style={styles.button} underlayColor="#C70F66"
        onPress={handleLogin}>
        <Text style={styles.buttonTitle}>ログイン</Text>
      </TouchableHighlight>
      <TouchableOpacity  style={styles.toSignup} onPress={switchToSignup}>
        <Text style={styles.signupText}>
          新規登録はこちら
        </Text>
      </TouchableOpacity>


    </View>
  );
}

export default LoginScreen
